<?phpfunction index(){}function addProductBrand(){	global $db,$request;	$comId = (int)$_SESSION[TB_PREFIX.'comId'];	$id = (int)$request['id'];	$pid = (int)$request['pid'];	$submit = (int)$request['submit'];	if($submit){    	$title = $request['title'];        $enTitle = $request['en_title'];    	$miaoshu = $request['miaoshu'];    	$parentId = (int)$request['parentId'];    	$enMiaoshu = $request['en_miaoshu'];        $isIndex = (int)$request['is_index'];    	$originalPic = $request['originalPic'];    	$ext_originalPic = $request['ext_originalPic'];    	$backimg = $request['backimg'];        	if(empty($id)){        		$ifhas = $db->get_var("select id from demo_product_brand where comId=$comId and title='$title'");        		if(!empty($ifhas)){        			echo '<script>alert("您已经创建过这个品牌了！");history.go(-1);</script>';        			exit;        		}        		$db->query("insert into demo_product_brand(comId,title,en_title,miaoshu,en_miaoshu,parentId,originalPic,ext_originalPic,backimg,is_index) value($comId,'$title','$enTitle','$miaoshu', '$enMiaoshu',$parentId, '$originalPic','$ext_originalPic','$backimg', $isIndex)");    		    		$id = $db->get_var("select last_insert_id();");    		$db->query("update demo_product_brand set ordering = $id where id = $id");    	}else{        		$db->query("update demo_product_brand set title='$title',en_title='$enTitle',miaoshu='$miaoshu',en_miaoshu='$enMiaoshu',parentId=$parentId, originalPic='$originalPic',backimg='$backimg',ext_originalPic='$ext_originalPic',is_index=$isIndex where id=$id and comId=$comId");        	}        	redirect("?m=system&s=product_brand");	}}function totop(){	global $db,$request;	$comId = (int)$_SESSION[TB_PREFIX.'comId'];	$id = (int)$request['id'];	$maxOrdering = $db->get_var("select ordering from demo_product_brand where comId=$comId order by ordering desc limit 1");	$maxOrdering+=1;	$db->query("update demo_product_brand set ordering=$maxOrdering where id=$id");	redirect("?m=system&s=product_brand");}function move(){    global $db,$request;    	$comId = (int)$_SESSION[TB_PREFIX.'comId'];	$id = (int)$request['id'];	$type = (int)$request['type'];//类型：0-向下  1-向上		$currentChannel = $db->get_row("select * from demo_product_brand where id = $id");	if($type){//1-向上	    $move = $db->get_row("select * from demo_product_brand where  ordering > $currentChannel->ordering order by ordering asc ");	}else{	    $move = $db->get_row("select * from demo_product_brand where  ordering < $currentChannel->ordering order by ordering desc ");	}	if($move){	    $db->query("update demo_product_brand set ordering = $move->ordering where id = $id");	    $db->query("update demo_product_brand set ordering = $currentChannel->ordering where id = $move->id");	}	redirect("?m=system&s=product_brand&id=$id");}function delBrand(){	global $db,$request;	$id = (int)$request['id'];	$ifhas = $db->get_var("select id from demo_product where brandId=$id limit 1");	if(!empty($ifhas)){		echo '{"code":0,"message":"删除品牌前请先转移品牌下的产品！"}';	}else{		$db->query("delete from demo_product_brand where id=$id");		echo '{"code":1,"message":"删除成功！","ids":"'.$id.'"}';	}	exit;}